// ============================================================================
// CAL PROSPECTS v6 - Extract Prospects
// ============================================================================

const response = $input.first().json;
const buildPromptData = $('Build xAI Prompt v6').first().json;
const expectedSearches = buildPromptData.expectedSearches?.total || 12;

if (!response.body || !response.body.output) {
  console.log('No valid response from xAI');
  return { 
    json: { 
      prospects: [], 
      error: 'No valid response from xAI', 
      raw: response,
      searchCallCount: 0,
      expectedSearches
    } 
  };
}

const output = response.body.output;
const usage = response.body.usage || {};
const searchCallCount = usage.server_side_tool_usage_details?.x_search_calls || 0;

let textContent = '';
let annotations = [];

for (const item of output) {
  if (item.type === 'message' && item.content) {
    for (const block of item.content) {
      if (block.type === 'output_text' || block.type === 'text') {
        textContent = block.text || '';
        annotations = block.annotations || [];
        break;
      }
    }
  }
}

if (!textContent) {
  console.log('No text content in response');
  return { 
    json: { 
      prospects: [], 
      error: 'No text content in response', 
      raw: response.body,
      searchCallCount,
      expectedSearches
    } 
  };
}

let prospects = [];
try {
  let jsonMatch = textContent.match(/\[\s*\{[\s\S]*?\}\s*\]/g);

  if (jsonMatch) {
    const largestMatch = jsonMatch.reduce((a, b) => a.length > b.length ? a : b);
    prospects = JSON.parse(largestMatch);
  } else {
    const codeBlockMatch = textContent.match(/```(?:json)?\s*([\s\S]*?)```/);
    if (codeBlockMatch) {
      prospects = JSON.parse(codeBlockMatch[1].trim());
    } else {
      prospects = JSON.parse(textContent);
    }
  }
} catch (e) {
  console.log('Could not parse JSON, extracting from annotations');

  const urls = annotations
    .filter(a => a.type === 'url_citation')
    .map(a => a.url)
    .filter(url => url && url.includes('x.com'));

  if (urls.length > 0) {
    const uniqueUrls = [...new Set(urls)];
    for (const url of uniqueUrls) {
      const urlIndex = textContent.indexOf(url);
      if (urlIndex !== -1) {
        const start = Math.max(0, urlIndex - 500);
        const end = Math.min(textContent.length, urlIndex + 500);
        const context = textContent.substring(start, end);

        const usernameMatch = url.match(/x\.com\/([\w]+)\/status/);
        const username = usernameMatch ? '@' + usernameMatch[1] : 'unknown';

        let category = 'Civil Rights';
        if (/police|cop|officer|sheriff|arrest|search|seiz|warrant/i.test(context)) {
          category = 'Fourth Amendment';
        } else if (/cps|child protective|custody|family court|children|kids/i.test(context)) {
          category = 'Parental Rights';
        } else if (/speech|religion|assembly|press|protest/i.test(context)) {
          category = 'First Amendment';
        } else if (/due process|hearing|fair|procedure/i.test(context)) {
          category = 'Due Process';
        }

        prospects.push({
          post_url: url,
          username: username,
          constitutional_issue: context.substring(0, 200).replace(/\n/g, ' ').trim(),
          category: category,
          issue_details: context.replace(/\n/g, ' ').trim(),
          case_viability: 'Medium',
          viability_reasoning: 'Extracted from x_search results - needs manual review',
          _extracted_fallback: true
        });
      }
    }
  }
}

const seen = new Set();
prospects = prospects.filter(p => {
  if (!p.post_url || seen.has(p.post_url)) return false;
  seen.add(p.post_url);
  return true;
});

// Cross-run deduplication using n8n static data
const staticData = $getWorkflowStaticData('global');
if (!staticData.seenUrls) staticData.seenUrls = {};

const beforeDedup = prospects.length;
prospects = prospects.filter(p => {
  if (staticData.seenUrls[p.post_url]) return false;
  staticData.seenUrls[p.post_url] = Date.now();
  return true;
});
const dedupRemoved = beforeDedup - prospects.length;

// Prune entries older than 7 days
const cutoff = Date.now() - 7 * 24 * 60 * 60 * 1000;
for (const [url, ts] of Object.entries(staticData.seenUrls)) {
  if (ts < cutoff) delete staticData.seenUrls[url];
}

console.log(`Extracted ${prospects.length} prospects (${dedupRemoved} duplicates removed) from ${searchCallCount} searches`);

const searchWarning = searchCallCount < expectedSearches 
  ? `WARNING: Only ${searchCallCount} of ${expectedSearches} searches executed` 
  : null;

return {
  json: {
    prospects,
    stats: {
      total: prospects.length,
      dedupRemoved,
      searchCalls: searchCallCount,
      expectedSearches,
      searchWarning,
      tokens: usage.total_tokens || 0,
      costUsd: (usage.cost_in_usd_ticks || 0) / 1e9
    },
    rawText: textContent.substring(0, 8000),
    annotations: annotations.slice(0, 100)
  }
};

// ============================================================================
// CAL PROSPECTS v6 - Validate & Score
// ============================================================================

// ============================================================================
// SCORING CONFIG
// ============================================================================
const SCORING = {
  baseScore: 50,
  weights: {
    violationClarity: { max: 20, perMatch: 4 },
    governmentNexus: { max: 20, perMatch: 5 },
    helpSeeking: { strong: 15, weak: 10 },
    firstPerson: 5,
    damages: 10,
    recency: { today: 10, yesterday: 7, thisWeek: 5 },
    specificDetails: { max: 10, officer: 4, department: 3, location: 3 },
    engagement: { highThreshold: 500, lowThreshold: 50, highPenalty: -10, lowBonus: 5 },
  },
  tiers: { high: 85, medium: 65 },
};

const input = $input.first().json;
const prospects = input.prospects || [];
const stats = input.stats || {};

const URL_PATTERN = /^https:\/\/(x\.com|twitter\.com)\/([A-Za-z0-9_]{1,15})\/status\/(\d{18,20})$/;
const URL_PATTERN_ALT = /^https:\/\/(x\.com|twitter\.com)\/i\/status\/(\d{18,20})$/;

const FAKE_HANDLE_PATTERNS = [
  /^@?user\d*$/i, /^@?test\d*$/i, /^@?example\d*$/i,
  /^@?sample\d*$/i, /^@?demo\d*$/i, /^@?fake\d*$/i,
  /^@?placeholder\d*$/i, /^@?person\d*$/i
];

function isFakeHandle(handle) {
  if (!handle) return true;
  const normalized = handle.replace("@", "").toLowerCase();
  return FAKE_HANDLE_PATTERNS.some(p => p.test(normalized));
}

function validateProspect(p) {
  const errors = [];

  if (!p.post_url) {
    errors.push("Missing post URL");
  } else if (!URL_PATTERN.test(p.post_url) && !URL_PATTERN_ALT.test(p.post_url)) {
    const shortIdMatch = p.post_url.match(/\/status\/(\d{1,17})$/);
    if (shortIdMatch) {
      errors.push(`Invalid status ID (${shortIdMatch[1].length} digits) - likely hallucination`);
    } else {
      errors.push("Invalid X.com URL format");
    }
  }

  if (p.username && p.username !== "unknown" && p.username !== "@i") {
    if (isFakeHandle(p.username)) {
      errors.push("Fake/placeholder username detected");
    }
  }

  if (!p.constitutional_issue || p.constitutional_issue.length < 10) {
    errors.push("Missing or too short constitutional issue");
  }

  return errors;
}

function scoreProspect(p) {
  let score = SCORING.baseScore;
  const factors = [];

  const content = ((p.constitutional_issue || "") + " " + (p.issue_details || "") + " " + (p.full_text || "")).toLowerCase();

  // VIOLATION CLARITY (+20 max)
  const violationWords = [
    "arrest", "detained", "searched", "seized", "warrant", "rights", 
    "violated", "unconstitutional", "illegal", "unlawful", "brutality", 
    "excessive force", "false arrest", "wrongful", "misconduct"
  ];
  const violationMatches = violationWords.filter(w => content.includes(w)).length;
  score += Math.min(SCORING.weights.violationClarity.max, violationMatches * SCORING.weights.violationClarity.perMatch);
  if (violationMatches >= 3) factors.push("Clear violation language");

  // GOVERNMENT NEXUS (+20 max)
  const govWords = [
    "police", "officer", "cop", "sheriff", "cps", "child protective", 
    "court", "judge", "city", "county", "state", "federal", "deputy", 
    "trooper", "detective", "department"
  ];
  const govMatches = govWords.filter(w => content.includes(w)).length;
  score += Math.min(SCORING.weights.governmentNexus.max, govMatches * SCORING.weights.governmentNexus.perMatch);
  if (govMatches >= 2) factors.push("Government actor identified");

  // HELP-SEEKING (+15)
  const helpSeekingPatterns = [
    /\b(what can i do|what should i do|what are my options|need advice)\b/i,
    /\b(any lawyers|need a lawyer|looking for (an? )?attorney)\b/i,
    /\b(is this legal|can i sue|what recourse|who can help)\b/i,
    /\b(please help|someone help|any advice|seeking help)\b/i
  ];
  if (helpSeekingPatterns.some(p => p.test(content))) {
    score += SCORING.weights.helpSeeking.strong;
    factors.push("Actively seeking help");
  } else if (/\b(help|advice|lawyer|attorney|legal)\b/i.test(content)) {
    score += SCORING.weights.helpSeeking.weak;
    factors.push("Help-related language");
  }

  // FIRST PERSON (+5)
  if (/\b(my|i|me|we|our)\b.*\b(was|got|they|police|officer|cps|court)\b/i.test(content)) {
    score += SCORING.weights.firstPerson;
    factors.push("First-person account");
  }

  // DAMAGES (+10)
  const damageWords = [
    "fired", "suspended", "arrested", "jail", "injured", "taken", 
    "seized", "lost", "children", "kids", "custody", "hospital", 
    "beaten", "tased", "hurt"
  ];
  if (damageWords.some(w => content.includes(w))) {
    score += SCORING.weights.damages;
    factors.push("Damages apparent");
  }

  // RECENCY (+10)
  const recency = (p.incident_recency || "").toLowerCase();
  if (recency.includes("today") || recency.includes("just") || recency.includes("hour")) {
    score += SCORING.weights.recency.today;
    factors.push("Very recent (today)");
  } else if (recency.includes("yesterday")) {
    score += SCORING.weights.recency.yesterday;
    factors.push("Recent (yesterday)");
  } else if (/\b(monday|tuesday|wednesday|thursday|friday|this week)\b/i.test(content)) {
    score += SCORING.weights.recency.thisWeek;
    factors.push("Recent timeline");
  }

  // SPECIFIC DETAILS (+10)
  let detailBonus = 0;
  if (/\b(officer|deputy|detective)\s+[A-Z][a-z]+/i.test(content)) detailBonus += SCORING.weights.specificDetails.officer;
  if (/\b[A-Z][a-z]+\s+(police|sheriff|pd|department)\b/i.test(content)) detailBonus += SCORING.weights.specificDetails.department;
  if (/\b[A-Z][a-z]+,?\s+(county|city|state)\b/i.test(content)) detailBonus += SCORING.weights.specificDetails.location;
  score += Math.min(SCORING.weights.specificDetails.max, detailBonus);
  if (detailBonus >= 5) factors.push("Specific details");

  // ENGAGEMENT SCORING
  const engagement = p.engagement || {};
  const likes = engagement.likes || 0;

  if (likes > SCORING.weights.engagement.highThreshold) {
    score += SCORING.weights.engagement.highPenalty;
    factors.push("High engagement penalty");
  } else if (likes < SCORING.weights.engagement.lowThreshold) {
    score += SCORING.weights.engagement.lowBonus;
    factors.push("Low engagement (real help-seeker)");
  }

  // DETERMINE TIER
  let tier = "Low";
  if (score >= SCORING.tiers.high) tier = "High";
  else if (score >= SCORING.tiers.medium) tier = "Medium";

  return { score, tier, factors };
}

const validated = [];
const rejected = [];

for (const p of prospects) {
  const errors = validateProspect(p);
  if (errors.length > 0) {
    rejected.push({ ...p, rejection_reasons: errors });
    continue;
  }

  const scoring = scoreProspect(p);

  validated.push({
    ...p,
    viability_score: scoring.score,
    viability_tier: scoring.tier,
    strength_factors: scoring.factors,
    processed_at: new Date().toISOString()
  });
}

validated.sort((a, b) => (b.viability_score || 0) - (a.viability_score || 0));

const summary = {
  total_from_search: prospects.length,
  validated: validated.length,
  rejected: rejected.length,
  by_tier: {
    high: validated.filter(p => p.viability_tier === "High").length,
    medium: validated.filter(p => p.viability_tier === "Medium").length,
    low: validated.filter(p => p.viability_tier === "Low").length
  },
  search_stats: stats
};

console.log(`Validated: ${validated.length}, Rejected: ${rejected.length}`);

return {
  json: {
    prospects: validated,
    rejected,
    summary,
    timestamp: new Date().toISOString()
  }
};

// ============================================================================
// CAL PROSPECTS v6 - Build Email & PDF
// ============================================================================

const input = $input.first().json;
const prospects = input.prospects || [];
const rejected = input.rejected || [];
const summary = input.summary || {};
const timestamp = input.timestamp || new Date().toISOString();

const date = new Date(timestamp).toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

const time = new Date(timestamp).toLocaleTimeString('en-US', {
  hour: 'numeric',
  minute: '2-digit',
  hour12: true,
  timeZone: 'America/Chicago'
});

const highPriority = prospects.filter(p => p.viability_tier === 'High');
const hasHighPriority = highPriority.length > 0;
const searchWarning = summary.search_stats?.searchWarning;

let html = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; line-height: 1.6; color: #333; max-width: 800px; margin: 0 auto; padding: 20px; }
    .header { background: linear-gradient(135deg, #1a365d 0%, #2c5282 100%); color: white; padding: 30px; border-radius: 8px 8px 0 0; }
    .header h1 { margin: 0; font-size: 24px; }
    .header p { margin: 10px 0 0; opacity: 0.9; }
    .version-badge { display: inline-block; background: rgba(255,255,255,0.2); padding: 2px 8px; border-radius: 4px; font-size: 11px; margin-left: 10px; }
    .alert-banner { background: #22543d; color: #c6f6d5; padding: 15px 20px; font-weight: 600; text-align: center; }
    .warning-banner { background: #744210; color: #fefcbf; padding: 10px 20px; font-size: 13px; text-align: center; }
    .summary { background: #f7fafc; padding: 20px; border-left: 4px solid #3182ce; margin: 20px 0; }
    .summary-grid { display: grid; grid-template-columns: repeat(4, 1fr); gap: 15px; text-align: center; }
    .stat { padding: 10px; }
    .stat-value { font-size: 28px; font-weight: bold; color: #2c5282; }
    .stat-value.highlight { color: #22543d; }
    .stat-label { font-size: 12px; text-transform: uppercase; color: #718096; }
    .prospect { background: white; border: 1px solid #e2e8f0; border-radius: 8px; padding: 20px; margin: 15px 0; }
    .prospect.high-priority { border: 2px solid #22543d; background: linear-gradient(to right, #f0fff4, white); }
    .prospect-header { display: flex; justify-content: space-between; align-items: flex-start; margin-bottom: 15px; }
    .prospect-title { font-size: 16px; font-weight: 600; color: #1a365d; }
    .tier { padding: 4px 12px; border-radius: 20px; font-size: 12px; font-weight: 600; }
    .tier-high { background: #22543d; color: white; }
    .tier-medium { background: #fefcbf; color: #744210; }
    .tier-low { background: #fed7d7; color: #822727; }
    .category { display: inline-block; background: #ebf4ff; color: #3182ce; padding: 2px 8px; border-radius: 4px; font-size: 12px; margin-bottom: 10px; }
    .summary-text { color: #4a5568; font-size: 14px; margin: 10px 0; }
    .factors { margin-top: 10px; }
    .factor { display: inline-block; background: #c6f6d5; color: #22543d; padding: 2px 8px; border-radius: 4px; font-size: 11px; margin: 2px; }
    .link { color: #3182ce; text-decoration: none; font-weight: 600; }
    .footer { text-align: center; padding: 20px; color: #718096; font-size: 12px; border-top: 1px solid #e2e8f0; margin-top: 30px; }
    .no-prospects { text-align: center; padding: 40px; color: #718096; }
    .pdf-note { background: #ebf4ff; padding: 15px; border-radius: 8px; margin: 20px 0; text-align: center; color: #2c5282; }
  </style>
</head>
<body>
  <div class="header">
    <h1>🏛️ CAL Prospect Report <span class="version-badge">v6</span></h1>
    <p>${date} • ${time} CST</p>
  </div>
`;

if (hasHighPriority) {
  html += `<div class="alert-banner"><span>⚡</span> ${highPriority.length} HIGH-PRIORITY PROSPECT${highPriority.length > 1 ? 'S' : ''} FOUND</div>`;
}

if (searchWarning) {
  html += `<div class="warning-banner">⚠️ ${searchWarning}</div>`;
}

html += `
  <div class="summary">
    <div class="summary-grid">
      <div class="stat"><div class="stat-value">${summary.validated || 0}</div><div class="stat-label">New Prospects</div></div>
      <div class="stat"><div class="stat-value ${hasHighPriority ? 'highlight' : ''}">${summary.by_tier?.high || 0}</div><div class="stat-label">High Priority</div></div>
      <div class="stat"><div class="stat-value">${summary.by_tier?.medium || 0}</div><div class="stat-label">Medium Priority</div></div>
      <div class="stat"><div class="stat-value">${summary.search_stats?.searchCalls || 0}/${summary.search_stats?.expectedSearches || 12}</div><div class="stat-label">Searches Run</div></div>
    </div>
  </div>
  <div class="pdf-note">📎 <strong>Full detailed report attached as PDF</strong></div>
`;

if (prospects.length === 0) {
  html += `<div class="no-prospects"><h2>No New Prospects This Run</h2><p>No qualifying posts were found.</p></div>`;
} else {
  const emailProspects = prospects.filter(p => p.viability_tier !== 'Low').slice(0, 10);
  html += `<h2 style="color: #1a365d; margin-top: 30px;">📋 Quick Summary (${emailProspects.length} of ${prospects.length})</h2>`;

  for (const p of emailProspects) {
    const isHigh = p.viability_tier === 'High';
    const tierClass = isHigh ? 'tier-high' : p.viability_tier === 'Medium' ? 'tier-medium' : 'tier-low';
    html += `
    <div class="prospect ${isHigh ? 'high-priority' : ''}">
      <div class="prospect-header">
        <div><div class="prospect-title">${isHigh ? '⚡ ' : ''}${p.username || 'Unknown'}</div><span class="category">${p.category || 'Civil Rights'}</span></div>
        <span class="tier ${tierClass}">${p.viability_tier} (${p.viability_score})</span>
      </div>
      <div class="summary-text">${(p.constitutional_issue || '').substring(0, 200)}${(p.constitutional_issue || '').length > 200 ? '...' : ''}</div>
      ${p.strength_factors?.length ? `<div class="factors">${p.strength_factors.map(f => `<span class="factor">✓ ${f}</span>`).join('')}</div>` : ''}
      <div style="margin-top: 15px;"><a href="${p.post_url}" class="link" target="_blank">View Post →</a></div>
    </div>`;
  }

  if (prospects.length > emailProspects.length) {
    html += `<p style="text-align: center; color: #718096;">+ ${prospects.length - emailProspects.length} more in PDF</p>`;
  }
}

html += `<div class="footer"><p>CAL Prospect Scanner v6 • xAI Grok x_search</p><p>📊 ${summary.search_stats?.searchCalls || 0} searches | ${(summary.search_stats?.tokens || 0).toLocaleString()} tokens | $${(summary.search_stats?.costUsd || 0).toFixed(3)}</p></div></body></html>`;

// PDF HTML
let pdfHtml = `<!DOCTYPE html><html><head><style>
@page { size: A4; margin: 0.75in; }
body { font-family: Georgia, serif; line-height: 1.8; color: #1a1a1a; font-size: 11pt; }
h1 { font-size: 24pt; color: #1a365d; border-bottom: 2px solid #1a365d; padding-bottom: 10px; }
h2 { font-size: 16pt; color: #2c5282; margin-top: 30px; border-bottom: 1px solid #e2e8f0; padding-bottom: 5px; }
.meta { color: #718096; font-size: 10pt; margin-bottom: 20px; }
.summary-box { background: #f7fafc; border: 1px solid #e2e8f0; padding: 15px; margin: 20px 0; }
.prospect-card { border: 1px solid #e2e8f0; padding: 20px; margin: 20px 0; page-break-inside: avoid; }
.prospect-card.high { border-left: 4px solid #22543d; background: #f0fff4; }
.prospect-card.medium { border-left: 4px solid #d69e2e; }
.tier-badge { display: inline-block; padding: 2px 10px; border-radius: 3px; font-size: 10pt; font-weight: bold; }
.tier-badge.high { background: #22543d; color: white; }
.tier-badge.medium { background: #d69e2e; color: white; }
.field-label { font-weight: bold; color: #4a5568; }
.post-text { background: #f7fafc; padding: 15px; border-left: 3px solid #3182ce; margin: 10px 0; font-style: italic; }
.factor { display: inline-block; background: #e2e8f0; padding: 2px 8px; margin: 2px; border-radius: 3px; font-size: 9pt; }
.url { color: #3182ce; word-break: break-all; }
.footer { margin-top: 40px; padding-top: 20px; border-top: 1px solid #e2e8f0; font-size: 9pt; color: #718096; text-align: center; }
</style></head><body>
<h1>CAL Constitutional Law Firm<br>Prospect Intelligence Report v6</h1>
<div class="meta"><strong>Generated:</strong> ${date} at ${time} CST<br><strong>Period:</strong> Last 72 hours<br><strong>Searches:</strong> ${summary.search_stats?.searchCalls || 0}/${summary.search_stats?.expectedSearches || 12}</div>
<div class="summary-box"><h3 style="margin-top:0">Summary</h3><p><strong>${summary.validated || 0}</strong> prospects (${summary.by_tier?.high || 0} high, ${summary.by_tier?.medium || 0} medium, ${summary.by_tier?.low || 0} low). ${summary.rejected || 0} rejected.</p>${searchWarning ? `<p style="color:#c53030"><strong>Warning:</strong> ${searchWarning}</p>` : ''}</div>`;

function buildCard(p, tier) {
  return `<div class="prospect-card ${tier}"><h3>${p.username || 'Unknown'} <span class="tier-badge ${tier}">${p.viability_tier} (${p.viability_score})</span></h3>
<p><span class="field-label">Category:</span> ${p.category || 'Civil Rights'}</p>
<p><span class="field-label">Issue:</span><br>${p.constitutional_issue || 'Not specified'}</p>
${p.full_text ? `<div class="post-text">"${p.full_text}"</div>` : ''}
${p.issue_details ? `<p><span class="field-label">Details:</span><br>${p.issue_details}</p>` : ''}
${p.viability_reasoning ? `<p><span class="field-label">Assessment:</span><br>${p.viability_reasoning}</p>` : ''}
${p.strength_factors?.length ? `<p><span class="field-label">Factors:</span> ${p.strength_factors.map(f => `<span class="factor">✓ ${f}</span>`).join(' ')}</p>` : ''}
<p><span class="field-label">URL:</span><br><span class="url">${p.post_url}</span></p></div>`;
}

if (prospects.length === 0) {
  pdfHtml += `<h2>No Prospects Found</h2><p>No qualifying posts in this period.</p>`;
} else {
  if (highPriority.length > 0) {
    pdfHtml += `<h2>⚡ High-Priority (${highPriority.length})</h2>`;
    highPriority.forEach(p => pdfHtml += buildCard(p, 'high'));
  }
  const medium = prospects.filter(p => p.viability_tier === 'Medium');
  if (medium.length > 0) {
    pdfHtml += `<h2>Medium-Priority (${medium.length})</h2>`;
    medium.forEach(p => pdfHtml += buildCard(p, 'medium'));
  }
  const low = prospects.filter(p => p.viability_tier === 'Low');
  if (low.length > 0) {
    pdfHtml += `<h2>Low-Priority (${low.length})</h2>`;
    low.forEach(p => pdfHtml += buildCard(p, 'low'));
  }
}

pdfHtml += `<div class="footer"><p>CONFIDENTIAL - Attorney Work Product</p><p>CAL v6 • Report ID: ${$execution.id}</p></div></body></html>`;

